                    return count
                if index + 1 < len(arr) and (index + 1) not in use:
                    use.add(index + 1)
                    queue.append(index + 1)
                v = arr[index]
                for i in map[v]:
                    if i == len(arr) - 1:
                        return count
                    if i not in use:
                        use.add(i)
                        queue.append(i)
                del map[v]
