            free += time
            # 
            size = len(queue)
            # 
            use = [False] * (n + 1)
            for i in range(size):
                for num in maps[queue.popleft()]:
                    # 2
                    if use[num] is False and counts[num] < 2:
                        queue.append(num)
                        use[num] = True
                        counts[num] += 1

