import java.util.LinkedList;
import java.util.Queue;

class Solution {
    public int numEnclaves(int[][] grid) {
        boolean[][] use = new boolean[grid.length][grid[0].length];
        Queue<int[]> queue = new LinkedList<>();
        int xl = grid.length;
        int yl = grid[0].length;
        int count = 0;
        for (int i = 0; i < xl; i++) {
            if (grid[i][0] == 1) {
                queue.add(new int[]{i, 0});
                use[i][0] = true;
                count++;
            }
            if (grid[i][yl - 1] == 1 && !use[i][yl - 1]) {
                queue.add(new int[]{i, yl - 1});
                use[i][yl - 1] = true;
                count++;
            }
        }
        for (int i = 1; i < yl - 1; i++) {
            if (grid[0][i] == 1 && !use[0][i]) {
                queue.add(new int[]{0, i});
                use[0][i] = true;
                count++;
            }
            if (grid[xl - 1][i] == 1 && !use[xl - 1][i]) {
                queue.add(new int[]{xl - 1, i});
                use[xl - 1][i] = true;
                count++;
            }
        }
        int[] xp = new int[]{1, -1, 0, 0};
        int[] yp = new int[]{0, 0, 1, -1};
        while (!queue.isEmpty()) {
            int[] arr = queue.poll();
            int x = arr[0];
            int y = arr[1];
            for (int k = 0; k < 4; k++) {
                int nx = x + xp[k];
                int ny = y + yp[k];
                if (nx >= 0 && nx < grid.length && ny >= 0 && ny < grid[0].length && grid[nx][ny] == 1 && !use[nx][ny]) {
                    queue.add(new int[]{nx, ny});
                    use[nx][ny] = true;
                    count++;
                }
            }
        }
        int sum = 0;
        for (int[] ints : grid) {
            for (int j = 0; j < yl; j++) {
                if (ints[j] == 1) {
                    sum++;
                }
            }
        }
        return sum - count;
    }
}
