class Solution {
    public int maxNumberOfBalloons(String text) {
        int[] arrs = new int[26];
        for (char ch : text.toCharArray()) {
            arrs[ch - 'a']++;
        }
        int count = Math.min(arrs[0], arrs[1]);
        count = Math.min(count, arrs['l' - 'a'] / 2);
        count = Math.min(count, arrs['o' - 'a'] / 2);
        count = Math.min(count, arrs['n' - 'a']);
        return count;
    }
}
