class Solution {
    public String convertToBase7(int num) {
        boolean bl = num < 0;
        num = Math.abs(num);
        StringBuilder str = new StringBuilder();
        while (num >= 7) {
            str.insert(0, num % 7);
            num /= 7;
        }
        str.insert(0, num);
        if (bl) {
            str.insert(0, '-');
        }
        return str.toString();
    }
}
