class Solution {
    public int bestRotation(int[] nums) {
        int size = nums.length;
        int[] arrs = new int[size + 1];
        for (int i = 0; i < size; i++) {
            int left = (i + 1) % size;
            int right = (i - nums[i] + size) % size;
            if (left > right) {
                arrs[0]++;
                arrs[size]--;
            }
            arrs[left]++;
            arrs[right + 1]--;
        }
        for (int i = 1; i < size + 1; i++) {
            arrs[i] += arrs[i - 1];
        }
        int result = 0;
        for (int i = 1; i < size + 1; i++) {
            if (arrs[i] > arrs[result]) {
                result = i;
            }
        }
        return result;
    }
}
