class Solution {
    public int countPrimes(int n) {
        if (n <= 2) {
            return 0;
        }
        boolean[] nums = new boolean[n + 1];
        Arrays.fill(nums, true);
        nums[0] = false;
        nums[1] = false;
        int count = 0;
        int max = (int) Math.sqrt(n);
        for (int i = 2; i < n; i++) {
            if (nums[i]) {
                count++;
                if (i > max) {
                    continue;
                }
                for (int j = i; j * i < n; j++) {
                    nums[j * i] = false;
                }
            }
        }
        return count;
    }
}
