class Solution {
    public int countGoodRectangles(int[][] rectangles) {
        int maxLength = 0;
        int count = 0;
        for (int[] rectangle : rectangles) {
            int temp = Math.min(rectangle[0], rectangle[1]);
            if (temp == maxLength) {
                count++;
            } else if (temp > maxLength) {
                count = 1;
                maxLength = temp;
            }
        }
        return count;
    }
}
