class Solution {
    public int numberOfMatches(int n) {
        // 
        int sum = 0;
        while (n > 1) {
            if (n % 2 == 1) {
                // 
                // (n - 1) / 2
                sum += (n - 1) / 2;
                // (n - 1) / 2 + 1
                n = (n - 1) / 2 + 1;
            } else {
                // 
                // n / 2
                sum += n / 2;
                // n / 2
                n /= 2;
            }
        }
        return sum;
    }
}
