    def numberOfMatches(self, n: int) -> int:
        # 
        sums = 0
        while n > 1:
            if n % 2 == 1:
                # 
                # (n - 1) / 2
                sums += (n - 1) / 2
                # (n - 1) / 2 + 1
                n = (n - 1) / 2 + 1
            else:
                # 
                # n / 2
                sums += n / 2
                # n / 2
                n /= 2
        return int(sums)
