class Solution {
    public int countNumbersWithUniqueDigits(int n) {
        if (n == 0) {
            return 1;
        }
        if (n == 1) {
            return 10;
        }
        int sub = 9;
        int count = 10;
        int mul = 9;
        for (int i = 2; i <= n; i++) {
            count += mul * sub;
            mul *= sub;
            sub--;
        }
        return count;
    }
}
